lists.dump:
function() {
    var row; 
    send('[\n');
    while (row = getRow()) {
        send('   "'+row.key+'",\n');
    }
    send(']\n');
}

photo_ids.map:
function (doc) {
  if (doc.type && (typeof doc.type === 'string') && (doc.type === 'photo')) {
    if (!doc.hidden) {
      emit(doc.paths[0], null);
    }
  }
}

invalid_type.map:
function (doc) {
  if (!('type' in doc) || !(typeof doc.type === 'string')) 
    emit(doc, null);
  if (('type' in doc) && (typeof doc.type === 'string')) {
    if ((doc.type !== 'login_event') && (doc.type !== 'user') && (doc.type !== 'photo'))
      emit(doc, null);
  }
}

logins.map:
function (doc) {
  if (('type' in doc) && (typeof doc.type === 'string') && (doc.type === 'login_event'))
    emit(doc, [doc.username, doc.timestamp]);
}

missing_attachments.map:
function (doc) {
  if (doc.type && (typeof doc.type === 'string') && (doc.type === 'photo')) {
    if (!doc._attachments)
      emit(doc.paths[0], "all missing");
    else if (!doc._attachments.thumbnail)
      emit(doc.paths[0], "thumbnail missing");
    else if (!doc._attachments.web_image)
      emit(doc.paths[0], "web_image missing");
    else if (!doc._attachments.image)
      emit(doc.paths[0], "image missing");
  }
}

missing_tags.map:
function (doc) {
  if (doc.type && (typeof doc.type === 'string') && (doc.type === 'photo')) {
    if (!doc.tags)
      emit(doc.paths[0], "all tags missing");
    else {
      for (var i = 0; i < doc.tags.length; i++) {
        if (doc.tags[i].source === "rekognition")
          return;
      }
      emit(doc.paths[0], "missing rekognition tags");
    }
  }
}
